import (
	"encoding/json"
	"net/http"

	"github.com/globalsign/mgo/bson"
	"github.com/go-chi/chi"
	"github.com/go-chi/render"
)

// Data model
type product struct {
	ID		bson.ObjectId	`bson:"_id,omitempty" json:"_id"`
	Catid	float32		`json:"catid"`
	Productname	string		`json:"productname"`

	Catpro	bson.ObjectId	`bson:"catpro,omitempty" json:"catpro"`

}

// Functions

// CRUD METHODS
	
// CRUD - CREATE
func (config *Config) create(writer http.ResponseWriter, req *http.Request) {

	var body product
	json.NewDecoder(req.Body).Decode(&body)
	config.Database.C("product").Insert(body)
	render.JSON(writer, req, &body)
}

// CRUD - DELETE
func (config *Config) delete(writer http.ResponseWriter, req *http.Request) {

	id := chi.URLParam(req, "id")
	config.Database.C("product").RemoveId(bson.ObjectIdHex(id))
	render.JSON(writer, req, id)
}

// CRUD - FIND BY catpro
func (config *Config) findBycatpro(writer http.ResponseWriter, req *http.Request) {
	var result []product
	key := chi.URLParam(req, "key")
	err := config.Database.C("product").Find(bson.M{"catpro": bson.ObjectIdHex(key)}).All(&result)
	if err != nil {
		panic(err)
	}

	render.JSON(writer, req, result)
}

// CRUD - GET
func (config *Config) get(writer http.ResponseWriter, req *http.Request) {

	var result product
	id := chi.URLParam(req, "id")
	err := config.Database.C("product").FindId(bson.ObjectIdHex(id)).One(&result)
	if err != nil {
		panic(err)
	}

	render.JSON(writer, req, result)
}
	
// CRUD - GET LIST
func (config *Config) list(writer http.ResponseWriter, req *http.Request) {

	var result []product
	err := config.Database.C("product").Find(nil).All(&result)
	if err != nil {
		panic(err)
	}

	render.JSON(writer, req, result)
}

// CRUD - UPDATE
func (config *Config) update(writer http.ResponseWriter, req *http.Request) {

	id := chi.URLParam(req, "id")
	var body product
	json.NewDecoder(req.Body).Decode(&body)
	config.Database.C("product").UpdateId(bson.ObjectIdHex(id), body)
	render.JSON(writer, req, body)
}

